type Configuration = Partial<Callbacks> & Partial<UserInterfaceSettings> & Partial<Environment>;
type UserInterfaceSettings = {
    uiSettings: {
        recommendedPaymentMethod?: boolean;
        size?: 'small' | 'large';
        borderRadius?: number;
        merchantLogoUri?: string;
    };
};
type Environment = {
    production: boolean;
};
type Resource = {
    paymentId: string;
    resourceToken: string;
};
type Page = 'cancel' | 'providers' | 'consent' | 'waiting-bank' | 'qr-code' | 'qr-code-loader' | 'send-to-bank' | 'return-from-bank' | 'account-selection' | 'checkout' | 'learn-more';
type Callbacks = {
    onDone: (info: {
        resultStatus: 'success' | 'failed' | 'pending';
    }) => void;
    onCancel: () => void;
    onError: (error?: Error) => void;
    onPayButtonClicked: () => void;
    onNavigation: (page: Page) => void;
};

declare function initWebSdk({ onDone, onCancel, onError, onPayButtonClicked, onNavigation, ...options }: Configuration): {
    mount: (node: HTMLElement) => {
        start: (resource: Resource) => {
            cleanup: () => void;
        };
        cleanup: () => void;
    };
    start: (resource: Resource) => {
        cleanup: () => void;
    };
    cleanup: () => void;
    openLearnMoreModal: () => void;
};

export { initWebSdk };
